import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { diacritics } from '../data/gujaratiData';

const DiacriticsScreen = () => {
  const [selectedDiacritic, setSelectedDiacritic] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const handleDiacriticPress = (diacritic) => {
    setSelectedDiacritic(diacritic);
    setModalVisible(true);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>માત્રા</Text>
        <Text style={styles.englishTitle}>Diacritics</Text>
        <Text style={styles.subtitle}>ગુજરાતી માત્રા શીખો</Text>
        <Text style={styles.englishSubtitle}>Learn Gujarati Diacritics</Text>
      </View>

      <View style={styles.content}>
        <View style={styles.infoBox}>
          <Ionicons name="information-circle" size={24} color="#45B7D1" />
          <Text style={styles.infoText}>
            માત્રા એટલે સ્વરના ચિહ્નો જે વ્યંજન સાથે જોડાય છે. તમે તેમને ટચ કરીને સાંભળી શકો છો.
          </Text>
        </View>

        <View style={styles.diacriticsGrid}>
          {diacritics.map((diacritic, index) => (
            <TouchableOpacity
              key={index}
              style={styles.diacriticCard}
              onPress={() => handleDiacriticPress(diacritic)}
              activeOpacity={0.7}
            >
              <View style={styles.diacriticHeader}>
                <Text style={styles.diacriticSymbol}>{diacritic.symbol}</Text>
                <Text style={styles.diacriticName}>{diacritic.name}</Text>
              </View>
              <Text style={styles.diacriticPronunciation}>{diacritic.pronunciation}</Text>
              <Text style={styles.diacriticEnglish}>{diacritic.english}</Text>
            </TouchableOpacity>
          ))}
        </View>

        <View style={styles.exampleSection}>
          <Text style={styles.exampleTitle}>Examples with ક (ka)</Text>
          <View style={styles.examplesGrid}>
            <View style={styles.exampleCard}>
              <Text style={styles.exampleText}>ક + ા = કા</Text>
              <Text style={styles.examplePronunciation}>ka + aa = kaa</Text>
            </View>
            <View style={styles.exampleCard}>
              <Text style={styles.exampleText}>ક + િ = કિ</Text>
              <Text style={styles.examplePronunciation}>ka + i = ki</Text>
            </View>
            <View style={styles.exampleCard}>
              <Text style={styles.exampleText}>ક + ી = કી</Text>
              <Text style={styles.examplePronunciation}>ka + ii = kii</Text>
            </View>
            <View style={styles.exampleCard}>
              <Text style={styles.exampleText}>ક + ુ = કુ</Text>
              <Text style={styles.examplePronunciation}>ka + u = ku</Text>
            </View>
          </View>
        </View>
      </View>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedDiacritic && (
              <>
                <View style={styles.modalHeader}>
                  <Text style={styles.modalTitle}>{selectedDiacritic.symbol}</Text>
                  <TouchableOpacity
                    onPress={() => setModalVisible(false)}
                    style={styles.closeButton}
                  >
                    <Ionicons name="close" size={24} color="#666" />
                  </TouchableOpacity>
                </View>

                <View style={styles.modalBody}>
                  <View style={styles.nameSection}>
                    <Text style={styles.sectionTitle}>Name</Text>
                    <Text style={styles.nameText}>{selectedDiacritic.name}</Text>
                  </View>

                  <View style={styles.pronunciationSection}>
                    <Text style={styles.sectionTitle}>Pronunciation</Text>
                    <Text style={styles.pronunciationText}>{selectedDiacritic.pronunciation}</Text>
                  </View>

                  <View style={styles.englishSection}>
                    <Text style={styles.sectionTitle}>English Description</Text>
                    <Text style={styles.englishText}>{selectedDiacritic.english}</Text>
                  </View>

                  <TouchableOpacity style={styles.listenButton}>
                    <Ionicons name="volume-high" size={24} color="white" />
                    <Text style={styles.listenText}>Listen</Text>
                  </TouchableOpacity>
                </View>
              </>
            )}
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    backgroundColor: '#45B7D1',
    padding: 30,
    alignItems: 'center',
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 5,
  },
  englishTitle: {
    fontSize: 16,
    color: 'white',
    marginBottom: 15,
  },
  subtitle: {
    fontSize: 14,
    color: 'white',
    textAlign: 'center',
    marginBottom: 5,
  },
  englishSubtitle: {
    fontSize: 12,
    color: 'white',
    textAlign: 'center',
    opacity: 0.9,
  },
  content: {
    padding: 20,
  },
  infoBox: {
    backgroundColor: '#E3F2FD',
    borderRadius: 10,
    padding: 15,
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  infoText: {
    fontSize: 14,
    color: '#1976D2',
    marginLeft: 10,
    flex: 1,
  },
  diacriticsGrid: {
    marginBottom: 30,
  },
  diacriticCard: {
    backgroundColor: 'white',
    borderRadius: 15,
    padding: 20,
    marginBottom: 15,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.22,
    shadowRadius: 2.22,
  },
  diacriticHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  diacriticSymbol: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#45B7D1',
    marginRight: 15,
  },
  diacriticName: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    flex: 1,
  },
  diacriticPronunciation: {
    fontSize: 14,
    color: '#666',
    marginBottom: 5,
  },
  diacriticEnglish: {
    fontSize: 12,
    color: '#999',
    fontStyle: 'italic',
  },
  exampleSection: {
    backgroundColor: '#F3E5F5',
    borderRadius: 15,
    padding: 20,
  },
  exampleTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#7B1FA2',
    marginBottom: 15,
    textAlign: 'center',
  },
  examplesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  exampleCard: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 15,
    marginBottom: 10,
    width: '48%',
    alignItems: 'center',
  },
  exampleText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#7B1FA2',
    marginBottom: 5,
  },
  examplePronunciation: {
    fontSize: 12,
    color: '#666',
    textAlign: 'center',
  },
  modalOverlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContent: {
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 0,
    width: '85%',
    maxHeight: '70%',
  },
  modalHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  modalTitle: {
    fontSize: 48,
    fontWeight: 'bold',
    color: '#45B7D1',
  },
  closeButton: {
    padding: 5,
  },
  modalBody: {
    padding: 20,
  },
  nameSection: {
    marginBottom: 20,
  },
  pronunciationSection: {
    marginBottom: 20,
  },
  englishSection: {
    marginBottom: 30,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 8,
  },
  nameText: {
    fontSize: 18,
    color: '#666',
  },
  pronunciationText: {
    fontSize: 18,
    color: '#666',
  },
  englishText: {
    fontSize: 16,
    color: '#666',
    fontStyle: 'italic',
  },
  listenButton: {
    backgroundColor: '#45B7D1',
    borderRadius: 25,
    padding: 15,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
  },
  listenText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    marginLeft: 8,
  },
});

export default DiacriticsScreen;
