import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Modal,
  TextInput,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { words } from '../data/wordsData';

const WordsScreen = () => {
  const [selectedWord, setSelectedWord] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All', icon: 'grid' },
    { id: 'family', name: 'Family', icon: 'people' },
    { id: 'animals', name: 'Animals', icon: 'paw' },
    { id: 'colors', name: 'Colors', icon: 'color-palette' },
    { id: 'food', name: 'Food', icon: 'restaurant' },
    { id: 'nature', name: 'Nature', icon: 'leaf' },
    { id: 'body', name: 'Body', icon: 'body' },
    { id: 'verbs', name: 'Verbs', icon: 'play' },
    { id: 'nouns', name: 'Nouns', icon: 'book' },
  ];

  const filteredWords = words.filter(word => {
    const matchesSearch = word.word.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         word.english.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         word.pronunciation.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || word.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleWordPress = (word) => {
    setSelectedWord(word);
    setModalVisible(true);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>શબ્દો</Text>
        <Text style={styles.englishTitle}>Words</Text>
        <Text style={styles.subtitle}>ગુજરાતી શબ્દો શીખો</Text>
        <Text style={styles.englishSubtitle}>Learn Gujarati Words</Text>
      </View>

      <View style={styles.content}>
        <View style={styles.searchContainer}>
          <Ionicons name="search" size={20} color="#666" style={styles.searchIcon} />
          <TextInput
            style={styles.searchInput}
            placeholder="Search words..."
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>

        <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoriesContainer}>
          {categories.map((category) => (
            <TouchableOpacity
              key={category.id}
              style={[
                styles.categoryButton,
                selectedCategory === category.id && styles.selectedCategory
              ]}
              onPress={() => setSelectedCategory(category.id)}
            >
              <Ionicons 
                name={category.icon} 
                size={16} 
                color={selectedCategory === category.id ? 'white' : '#FFEAA7'} 
              />
              <Text style={[
                styles.categoryText,
                selectedCategory === category.id && styles.selectedCategoryText
              ]}>
                {category.name}
              </Text>
            </TouchableOpacity>
          ))}
        </ScrollView>

        <View style={styles.wordsGrid}>
          {filteredWords.map((word, index) => (
            <TouchableOpacity
              key={index}
              style={styles.wordCard}
              onPress={() => handleWordPress(word)}
              activeOpacity={0.7}
            >
              <Text style={styles.wordText}>{word.word}</Text>
              <Text style={styles.wordPronunciation}>{word.pronunciation}</Text>
              <Text style={styles.wordEnglish}>{word.english}</Text>
              <View style={styles.categoryTag}>
                <Text style={styles.categoryTagText}>{word.category}</Text>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {filteredWords.length === 0 && (
          <View style={styles.noResults}>
            <Ionicons name="search-outline" size={48} color="#ccc" />
            <Text style={styles.noResultsText}>No words found</Text>
            <Text style={styles.noResultsSubtext}>Try a different search term</Text>
          </View>
        )}
      </View>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedWord && (
              <>
                <View style={styles.modalHeader}>
                  <Text style={styles.modalTitle}>{selectedWord.word}</Text>
                  <TouchableOpacity
                    onPress={() => setModalVisible(false)}
                    style={styles.closeButton}
                  >
                    <Ionicons name="close" size={24} color="#666" />
                  </TouchableOpacity>
                </View>

                <View style={styles.modalBody}>
                  <View style={styles.pronunciationSection}>
                    <Text style={styles.sectionTitle}>Pronunciation</Text>
                    <Text style={styles.pronunciationText}>{selectedWord.pronunciation}</Text>
                  </View>

                  <View style={styles.englishSection}>
                    <Text style={styles.sectionTitle}>English Meaning</Text>
                    <Text style={styles.englishText}>{selectedWord.english}</Text>
                  </View>

                  <View style={styles.categorySection}>
                    <Text style={styles.sectionTitle}>Category</Text>
                    <View style={styles.modalCategoryTag}>
                      <Text style={styles.modalCategoryTagText}>{selectedWord.category}</Text>
                    </View>
                  </View>

                  <TouchableOpacity style={styles.listenButton}>
                    <Ionicons name="volume-high" size={24} color="white" />
                    <Text style={styles.listenText}>Listen</Text>
                  </TouchableOpacity>
                </View>
              </>
            )}
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
