import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { stories } from '../data/storiesData';

const StoriesScreen = () => {
  const [selectedStory, setSelectedStory] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const handleStoryPress = (story) => {
    setSelectedStory(story);
    setModalVisible(true);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>બાળવાર્તા</Text>
        <Text style={styles.englishTitle}>Children's Stories</Text>
        <Text style={styles.subtitle}>ગુજરાતી બાળવાર્તાઓ વાંચો</Text>
        <Text style={styles.englishSubtitle}>Read Gujarati Children's Stories</Text>
      </View>

      <View style={styles.content}>
        <View style={styles.infoBox}>
          <Ionicons name="information-circle" size={24} color="#DDA0DD" />
          <Text style={styles.infoText}>
            બાળવાર્તાઓ શીખવાનો સૌથી સરળ રસ્તો છે. તમે તેમને ટચ કરીને વાંચી શકો છો.
          </Text>
        </View>

        <View style={styles.storiesList}>
          {stories.map((story, index) => (
            <TouchableOpacity
              key={story.id}
              style={styles.storyCard}
              onPress={() => handleStoryPress(story)}
              activeOpacity={0.7}
            >
              <View style={styles.storyHeader}>
                <View style={styles.storyNumber}>
                  <Text style={styles.storyNumberText}>{story.id}</Text>
                </View>
                <View style={styles.storyTitleContainer}>
                  <Text style={styles.storyTitle}>{story.title}</Text>
                  <Text style={styles.storyEnglishTitle}>{story.englishTitle}</Text>
                </View>
                <Ionicons name="chevron-forward" size={24} color="#DDA0DD" />
              </View>

              <View style={styles.storyPreview}>
                <Text style={styles.storyPreviewText} numberOfLines={3}>
                  {story.content}
                </Text>
              </View>

              <View style={styles.storyFooter}>
                <View style={styles.moralTag}>
                  <Ionicons name="bulb" size={16} color="#DDA0DD" />
                  <Text style={styles.moralText}>Moral Story</Text>
                </View>
                <Text style={styles.readMore}>Read More →</Text>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        <View style={styles.practiceSection}>
          <Text style={styles.practiceTitle}>Practice Reading</Text>
          <Text style={styles.practiceText}>
            Try reading these stories aloud to improve your pronunciation!
          </Text>
          <TouchableOpacity style={styles.practiceButton}>
            <Ionicons name="play" size={20} color="white" />
            <Text style={styles.practiceButtonText}>Start Reading Practice</Text>
          </TouchableOpacity>
        </View>
      </View>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedStory && (
              <>
                <View style={styles.modalHeader}>
                  <View style={styles.modalTitleContainer}>
                    <Text style={styles.modalTitle}>{selectedStory.title}</Text>
                    <Text style={styles.modalEnglishTitle}>{selectedStory.englishTitle}</Text>
                  </View>
                  <TouchableOpacity
                    onPress={() => setModalVisible(false)}
                    style={styles.closeButton}
                  >
                    <Ionicons name="close" size={24} color="#666" />
                  </TouchableOpacity>
                </View>

                <ScrollView style={styles.modalBody}>
                  <View style={styles.storySection}>
                    <Text style={styles.sectionTitle}>ગુજરાતી વાર્તા</Text>
                    <Text style={styles.storyContent}>{selectedStory.content}</Text>
                  </View>

                  <View style={styles.englishSection}>
                    <Text style={styles.sectionTitle}>English Translation</Text>
                    <Text style={styles.englishContent}>{selectedStory.englishContent}</Text>
                  </View>

                  <View style={styles.moralSection}>
                    <Text style={styles.sectionTitle}>Moral of the Story</Text>
                    <View style={styles.moralContainer}>
                      <Ionicons name="bulb" size={20} color="#DDA0DD" />
                      <View style={styles.moralTextContainer}>
                        <Text style={styles.moralGujarati}>{selectedStory.moral}</Text>
                        <Text style={styles.moralEnglish}>{selectedStory.englishMoral}</Text>
                      </View>
                    </View>
                  </View>

                  <View style={styles.actionButtons}>
                    <TouchableOpacity style={styles.listenButton}>
                      <Ionicons name="volume-high" size={20} color="white" />
                      <Text style={styles.listenText}>Listen</Text>
                    </TouchableOpacity>

                    <TouchableOpacity style={styles.shareButton}>
                      <Ionicons name="share" size={20} color="#DDA0DD" />
                      <Text style={styles.shareText}>Share</Text>
                    </TouchableOpacity>
                  </View>
                </ScrollView>
              </>
            )}
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
};

