import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { barakhadi } from '../data/barakhadiData';

const BarakhadiScreen = () => {
  const [selectedSyllable, setSelectedSyllable] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const handleSyllablePress = (syllable) => {
    setSelectedSyllable(syllable);
    setModalVisible(true);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>બારાખડી</Text>
        <Text style={styles.englishTitle}>Barakhadi</Text>
        <Text style={styles.subtitle}>સિલેબલ ચાર્ટ શીખો</Text>
        <Text style={styles.englishSubtitle}>Learn Syllabic Chart</Text>
      </View>

      <View style={styles.content}>
        <View style={styles.infoBox}>
          <Ionicons name="information-circle" size={24} color="#96CEB4" />
          <Text style={styles.infoText}>
            બારાખડી એટલે વ્યંજન અને સ્વરના મેળાપથી બનતા સિલેબલ્સ. તમે તેમને ટચ કરીને સાંભળી શકો છો.
          </Text>
        </View>

        {barakhadi.map((row, rowIndex) => (
          <View key={rowIndex} style={styles.barakhadiRow}>
            <View style={styles.consonantHeader}>
              <Text style={styles.consonantLetter}>{row.consonant}</Text>
              <Text style={styles.consonantName}>Consonant</Text>
            </View>

            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
              <View style={styles.syllablesContainer}>
                {row.syllables.map((syllable, syllableIndex) => (
                  <TouchableOpacity
                    key={syllableIndex}
                    style={styles.syllableCard}
                    onPress={() => handleSyllablePress(syllable)}
                    activeOpacity={0.7}
                  >
                    <Text style={styles.syllableText}>{syllable.syllable}</Text>
                    <Text style={styles.syllablePronunciation}>{syllable.pronunciation}</Text>
                    <Text style={styles.syllableEnglish}>{syllable.english}</Text>
                  </TouchableOpacity>
                ))}
              </View>
            </ScrollView>
          </View>
        ))}

        <View style={styles.practiceSection}>
          <Text style={styles.practiceTitle}>Practice Barakhadi</Text>
          <Text style={styles.practiceText}>
            Try reading different combinations of consonants and vowels!
          </Text>
          <TouchableOpacity style={styles.practiceButton}>
            <Ionicons name="play" size={20} color="white" />
            <Text style={styles.practiceButtonText}>Start Practice</Text>
          </TouchableOpacity>
        </View>
      </View>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedSyllable && (
              <>
                <View style={styles.modalHeader}>
                  <Text style={styles.modalTitle}>{selectedSyllable.syllable}</Text>
                  <TouchableOpacity
                    onPress={() => setModalVisible(false)}
                    style={styles.closeButton}
                  >
                    <Ionicons name="close" size={24} color="#666" />
                  </TouchableOpacity>
                </View>

                <View style={styles.modalBody}>
                  <View style={styles.pronunciationSection}>
                    <Text style={styles.sectionTitle}>Pronunciation</Text>
                    <Text style={styles.pronunciationText}>{selectedSyllable.pronunciation}</Text>
                  </View>

                  <View style={styles.englishSection}>
                    <Text style={styles.sectionTitle}>English</Text>
                    <Text style={styles.englishText}>{selectedSyllable.english}</Text>
                  </View>

                  <TouchableOpacity style={styles.listenButton}>
                    <Ionicons name="volume-high" size={24} color="white" />
                    <Text style={styles.listenText}>Listen</Text>
                  </TouchableOpacity>
                </View>
              </>
            )}
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    backgroundColor: '#96CEB4',
    padding: 30,
    alignItems: 'center',
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 5,
  },
  englishTitle: {
    fontSize: 16,
    color: 'white',
    marginBottom: 15,
  },
  subtitle: {
    fontSize: 14,
    color: 'white',
    textAlign: 'center',
    marginBottom: 5,
  },
  englishSubtitle: {
    fontSize: 12,
    color: 'white',
    textAlign: 'center',
    opacity: 0.9,
  },
  content: {
    padding: 20,
  },
  infoBox: {
    backgroundColor: '#E8F5E8',
    borderRadius: 10,
    padding: 15,
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  infoText: {
    fontSize: 14,
    color: '#2E7D32',
    marginLeft: 10,
    flex: 1,
  },
  barakhadiRow: {
    backgroundColor: 'white',
    borderRadius: 15,
    padding: 15,
    marginBottom: 15,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.22,
    shadowRadius: 2.22,
  },
  consonantHeader: {
    alignItems: 'center',
    marginBottom: 15,
    paddingBottom: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  consonantLetter: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#96CEB4',
    marginBottom: 5,
  },
  consonantName: {
    fontSize: 12,
    color: '#666',
  },
  syllablesContainer: {
    flexDirection: 'row',
    paddingHorizontal: 5,
  },
  syllableCard: {
    backgroundColor: '#F8F9FA',
    borderRadius: 10,
    padding: 12,
    marginRight: 10,
    alignItems: 'center',
    minWidth: 60,
  },
  syllableText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#96CEB4',
    marginBottom: 4,
  },
  syllablePronunciation: {
    fontSize: 12,
    color: '#333',
    marginBottom: 2,
  },
  syllableEnglish: {
    fontSize: 10,
    color: '#666',
  },
  practiceSection: {
    backgroundColor: '#FFF3E0',
    borderRadius: 15,
    padding: 20,
    alignItems: 'center',
    marginTop: 10,
  },
  practiceTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#E65100',
    marginBottom: 8,
  },
  practiceText: {
    fontSize: 14,
    color: '#E65100',
    textAlign: 'center',
    marginBottom: 15,
  },
  practiceButton: {
    backgroundColor: '#FF9800',
    borderRadius: 25,
    paddingHorizontal: 20,
    paddingVertical: 10,
    flexDirection: 'row',
    alignItems: 'center',
  },
  practiceButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    marginLeft: 8,
  },
  modalOverlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContent: {
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 0,
    width: '85%',
    maxHeight: '70%',
  },
  modalHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  modalTitle: {
    fontSize: 48,
    fontWeight: 'bold',
    color: '#96CEB4',
  },
  closeButton: {
    padding: 5,
  },
  modalBody: {
    padding: 20,
  },
  pronunciationSection: {
    marginBottom: 20,
  },
  englishSection: {
    marginBottom: 30,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 8,
  },
  pronunciationText: {
    fontSize: 18,
    color: '#666',
  },
  englishText: {
    fontSize: 16,
    color: '#666',
    fontStyle: 'italic',
  },
  listenButton: {
    backgroundColor: '#96CEB4',
    borderRadius: 25,
    padding: 15,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
  },
  listenText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    marginLeft: 8,
  },
});

export default BarakhadiScreen;
